const formatter=new Intl.NumberFormat("en-US",{style:"currency",currency:"USD"});function setPlayerId(e){document.getElementById("playerId").innerHTML=e}function setPlayerBalance(e){document.getElementById("moneyCounter").innerHTML=formatter.format(e).replaceAll("$","")}function setAop(e){document.getElementById("aop").innerHTML=e}function setPostal(e){document.getElementById("postal").innerHTML=e}function setSpeedLimit(e){document.getElementById("speedLimit").innerHTML=e}window.addEventListener("message",e=>{switch(e.data.type){case"id":setPlayerId(e.data.id);break;case"balance":setPlayerBalance(e.data.balance);break;case"postal":setPostal(e.data.postal);break;case"aop":setAop(e.data.aop);break;case"speedlimit":setSpeedLimit(e.data.speedlimit)}});